db = SQLAlchemy()


class Recipe(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String(100), unique=True, nullable=False)
    page_link = db.Column(db.String(10000), unique=True, nullable=False)
    rating = db.Column(db.Integer)
    cost = db.Column(db.String(10), nullable=False)
    time = db.Column(db.Integer)  # in minutes
    tags = db.Column(db.String(100))  # comma separated strings values 
    def __repr__(self):
        return f"{self.name} - {self.page_link}"
    
class Item(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String(100), unique=True, nullable=False)
    description = db.Column(db.String(10000), unique=True, nullable=False)
    rating = db.Column(db.Integer)
    cost = db.Column(db.String(10), nullable=False)
    time = db.Column(db.Integer)  # in minutes



class User(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    username = db.Column(db.String(80), unique=True, nullable=False)
    email = db.Column(db.String(120), unique=True, nullable=False)
    password = db.Column(db.String(128), nullable=False)  # store hashed passwords
    profile_image = db.Column(db.String(128), nullable=False) # image url 
    bio = db.Column(db.String(128), nullable=False)
    tags = db.Column(db.String(128), nullable=False) # a predefinded list will be used later [vegan , meat , ...]

    def __repr__(self):

        return f"User: {self.username}"

